        type: String,
        required: true,
        trim: true,
        min: 3,
        max: 20,
    },
    gender: {
        type: String,
        enum: ['Male', 'Female'],
        required: true,
    },
    DOB: {
        type: String,
        required: true,
    },
    Location: {
        type: String,
    },
    email: {
        type: String,
        required: true,
        trim: true,
        unique: true,
        lowercase: true,
    },
    password : {
        type: String,
        required: true,

    },
    ConfirmPassword : {
        type: String,
