    },
    role : {
        type: String,
        enum: ['user', 'admin'],
        default: 'user',
    },
    contactNumber: {
        type: String,
    },
    avatar: {
        type: String,
        default: "https://image.shutterstock.com/image-vector/default-avatar-profile-icon-social-600w-1677509740.jpg"
    },
    createdAt:{
        type:Date,
        default: new Date()
    }

}, {timestamps: true});


const Users = mongoose.model('Users', userSchema)

export default Users;

